mapper.update:
update medicine set itemname = #{itemname},unit = #{unit},medtype = #{medtype},price = #{price},
size=#{size},medcat=#{medcat}
where itemcode = #{itemcode}

mapper.select:
select m.itemname, b.count, u.realname, p.prename, b.billdate, b.billid from (((bill b join prescription p on b.treatid=p.preid) join userrdept ur on p.uRid=ur.uRid) join user u on ur.userid=u.userid) join medicine m on b.itemcode=m.itemcode
and pid =#{pid}
and isPaid=1 and isDone=0 and treatcat='prescription' and totalprice>0

mapper.update:
update bill set isDone =1
where billid in

    #{ids}

mapper.select:
select m.itemname, b.count, u.realname, p.prename, b.billdate, b.billid from (((bill b join prescription p on b.treatid=p.preid) join userrdept ur on p.uRid=ur.uRid) join user u on ur.userid=u.userid) join medicine m on b.itemcode=m.itemcode
and pid =#{pid}
and isPaid=1 and isDone=1 and treatcat='prescription' and totalprice>0
