mapper.select:
select p.pid as pid, pname, pgender, page, pbirth, paddress, state from register r join userrdept ud on r.uRid = ud.uRid join user u on u.userid = ud.userid join patient p on p.pid = r.pid where u.userid = #{userid}
and p.state = #{state}
and p.state != '诊毕'
and TO_DAYS(r.date) = TO_DAYS(now())

select p.pid as pid, pname, pgender, page, pbirth, paddress, state from register r join userrdept ud on r.uRid = ud.uRid join dept d on d.deptcode = ud.deptcode join patient p on p.pid = r.pid where d.deptcode = #{deptcode}
and p.state = #{state}
and p.state != '诊毕'
and TO_DAYS(r.date) = TO_DAYS(now())
