mapper.select:
select p.pid as pid, pname, pgender, page, pbirth, paddress, state, regid from register r join userrdept ud on r.uRid = ud.uRid join user u on u.userid = ud.userid join patient p on p.pid = r.pid where u.userid = #{userid}
and r.state = #{state}
and r.state != 6
and TO_DAYS(r.date) = TO_DAYS(now())

select p.pid as pid, pname, pgender, page, pbirth, paddress, state, regid from register r join userrdept ud on r.uRid = ud.uRid join dept d on d.deptcode = ud.deptcode join patient p on p.pid = r.pid where d.deptcode = #{deptcode}
and r.state = #{state}
and r.state != 6
and TO_DAYS(r.date) = TO_DAYS(now())

select * from his.bill b join his.exam e on b.treatid = e.exid join his.patient p on b.pid = p.pid join his.non_medic n on n.itemcode = b.itemcode left join examImgs ei on ei.exid = e.exid
extype = #{extype} and treatcat = 'exam' and isPaid = 1 and e.state != 'Revoked'
and b.pid = #{pid}
