mapper.select:
select r.ruleid as ruleid, r.rulename as rulename, u.username as username, r.reglevel as reglevel, r.ration as ration, d.deptname as deptname, r.timecode as timecode from
((userrdept ud join user u on u.userid = ud.userid) join dept d on d.deptcode = ud.deptcode) join rule r on r.uRid = ud.uRid
where deptname = #{deptname}

mapper.insert:
insert into rule(rulename, uRid, timecode, reglevel, ration) values
(#{rulename}, (select uRid from user u join userRdept ud on u.userid = ud.userid where u.username=#{username}
and ud.deptcode=#{deptname}), #{timecode}, #{reglevel}, #{ration})
